    <span class="token keyword">public</span> <span class="token keyword">void</span> <span class="token function">setCheckoutTimestamp</span><span class="token punctuation">(</span><span class="token keyword">long</span> timestamp<span class="token punctuation">)</span> <span class="token punctuation">{</span>
