      <span class="token comment">//返回的类型为集合</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>returnsMany <span class="token operator">=</span> configuration<span class="token punctuation">.</span><span class="token function">getObjectFactory</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">.</span><span class="token function">isCollection</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">.</span>returnType<span class="token punctuation">)</span> <span class="token operator">||</span> <span class="token keyword">this</span><span class="token punctuation">.</span>returnType<span class="token punctuation">.</span><span class="token function">isArray</span><span class="token punctuation">(</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token comment">//返回的类型为Cursor</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>returnsCursor <span class="token operator">=</span> <span class="token class-name">Cursor</span><span class="token punctuation">.</span><span class="token keyword">class</span><span class="token punctuation">.</span><span class="token function">equals</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">.</span>returnType<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token comment">//返回的类型为Optional</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>returnsOptional <span class="token operator">=</span> <span class="token class-name">Optional</span><span class="token punctuation">.</span><span class="token keyword">class</span><span class="token punctuation">.</span><span class="token function">equals</span><span class="token punctuation">(</span><span class="token keyword">this</span><span class="token punctuation">.</span>returnType<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token comment">//返回的类型为map</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>mapKey <span class="token operator">=</span> <span class="token function">getMapKey</span><span class="token punctuation">(</span>method<span class="token punctuation">)</span><span class="token punctuation">;</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>returnsMap <span class="token operator">=</span> <span class="token keyword">this</span><span class="token punctuation">.</span>mapKey <span class="token operator">!=</span> <span class="token keyword">null</span><span class="token punctuation">;</span>
      <span class="token keyword">this</span><span class="token punctuation">.</span>rowBoundsIndex <span class="token operator">=</span> <span class="token function">getUniqueParamIndex</span><span class="token punctuation">(</span>method<span class="token punctuation">,</span> <span class="token class-name">RowBounds</span><span class="token punctuation">.</span><span class="token keyword">class</span><span class="token punctuation">)</span><span class="token punctuation">;</span>
