      <span class="token keyword">this</span><span class="token punctuation">.</span>paramNameResolver <span class="token operator">=</span> <span class="token keyword">new</span> <span class="token class-name">ParamNameResolver</span><span class="token punctuation">(</span>configuration<span class="token punctuation">,</span> method<span class="token punctuation">)</span><span class="token punctuation">;</span>
