  <span class="token punctuation">}</span>

  <span class="token keyword">private</span> <span class="token keyword">void</span> <span class="token function">applyConfiguration</span><span class="token punctuation">(</span><span class="token class-name">SqlSessionFactoryBean</span> factory<span class="token punctuation">)</span> <span class="token punctuation">{</span>
